import React, { useEffect, useState } from 'react';
import api from '../api/api';
import Sidebar from './Sidebar';

function Dashboard() {
  const [labours, setLabours] = useState([]);
  const officerId = localStorage.getItem('officer_id');

  const fetchLabours = async () => {
    try {
      const res = await api.get(`/labour/2`);  // Assuming '2' is officer-specific endpoint
      setLabours(res.data.labours || []);      // FIX: access the array
    } catch (err) {
      console.error(err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await api.delete(`/labour/${id}`);       // FIX: Correct DELETE route for labour
      fetchLabours();
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchLabours();
  }, []);

  return (
    <div className="flex">
      <Sidebar />
      <div className="flex-1 p-6">
        <h2 className="text-2xl font-bold mb-4">Labour List</h2>
        <table className="min-w-full border border-gray-300">
          <thead className="bg-gray-100">
            <tr>
              <th className="border p-2">Name</th>
              <th className="border p-2">Father Name</th>
              <th className="border p-2">Contact</th>
              <th className="border p-2">Aadhaar</th>
              <th className="border p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {labours.length === 0 ? (
              <tr>
                <td colSpan="5" className="text-center p-4">No labours found.</td>
              </tr>
            ) : (
              labours.map((labour) => (
                <tr key={labour.id} className="odd:bg-white even:bg-gray-50">
                  <td className="border p-2">{labour.name}</td>
                  <td className="border p-2">{labour.father_name}</td>
                  <td className="border p-2">{labour.contact_number}</td>
                  <td className="border p-2">{labour.aadhaar_number}</td>
                  <td className="border p-2 space-x-2">
                    <button className="bg-yellow-500 text-white px-2 py-1 rounded">Edit</button>
                    <button
                      onClick={() => handleDelete(labour.id)}
                      className="bg-red-600 text-white px-2 py-1 rounded"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Dashboard;
